import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getBarbers } from '../api/api';

export default function BarberProfile() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [barber, setBarber] = useState(null);

  useEffect(() => {
    getBarbers().then(res => {
      const b = res.data.find(b => b.id === parseInt(id));
      setBarber(b);
    });
  }, [id]);

  if (!barber) return <p className="text-center mt-10">טוען פרטי ספר...</p>;

  return (
    <div className="max-w-md mx-auto p-4 text-right">
      <button
        onClick={() => navigate(-1)}
        className="mb-4 text-blue-600 hover:underline"
      >
        ← חזור
      </button>

      <div className="bg-white shadow-md rounded-xl overflow-hidden">
        <div className="flex flex-col items-center p-6">
          <img
            src={barber.image || '/placeholder.png'}
            alt={barber.name}
            className="w-32 h-32 rounded-full border-4 border-blue-500 mb-4"
          />
          <h2 className="text-2xl font-bold mb-1">{barber.name}</h2>
          <p className="text-gray-500 text-center">{barber.bio}</p>
        </div>

        <div className="p-6 border-t">
          <h3 className="font-semibold mb-2 text-lg">שירותים</h3>
          <ul className="divide-y divide-gray-200">
            {barber.services?.map(s => (
              <li
                key={s.id}
                className="py-2 flex justify-between text-gray-700"
              >
                <span>{s.name}</span>
                <span className="font-semibold">{s.price} ₪</span>
              </li>
            ))}
          </ul>

          <button
            onClick={() => navigate(`/book/${barber.id}`)}
            className="mt-6 w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 rounded-lg transition duration-200"
          >
            קבע תור
          </button>
        </div>
      </div>
    </div>
  );
}
