import React, { useEffect, useState } from 'react';
import { getAppointments, cancelAppointment } from '../api/api';

function MyAppointments() {
  const [appointments, setAppointments] = useState([]);
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (token) {
      getAppointments(token)
        .then(res => setAppointments(res.data))
        .catch(err => console.error(err));
    }
  }, [token]);

  const handleCancel = async (id) => {
    try {
      await cancelAppointment(id, token);
      setAppointments(prev => prev.filter(a => a.id !== id));
    } catch (err) {
      console.error('שגיאה בביטול התור:', err);
    }
  };

  return (
    <div>
      <h1>התורים שלי</h1>
      {appointments.length === 0 ? (
        <p>אין תורים פעילים.</p>
      ) : (
        <ul>
          {appointments.map(a => (
            <li key={a.id} style={{ marginBottom: '10px', border: '1px solid #ccc', padding: '5px' }}>
              <p><strong>ספר:</strong> {a.barberName}</p>
              <p><strong>שירות:</strong> {a.serviceName}</p>
              <p><strong>תאריך ושעה:</strong> {new Date(a.start_datetime).toLocaleString('he-IL')}</p>
              <button onClick={() => handleCancel(a.id)}>בטל תור</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default MyAppointments;
