import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getBarber, createAppointment } from '../api/api';
import AppointmentForm from '../components/AppointmentForm';

function BookAppointment() {
  const { barberId } = useParams();
  const [barber, setBarber] = useState(null);

  useEffect(() => {
    getBarber(barberId).then(res => setBarber(res.data));
  }, [barberId]);

  const handleSubmit = async (data) => {
    await createAppointment(data);
    alert('התור נקבע בהצלחה!');
  };

  if (!barber) return <div>טוען...</div>;

  return (
    <div>
      <h1>קביעת תור ל-{barber.name}</h1>
      <AppointmentForm barberId={barberId} services={barber.services} onSubmit={handleSubmit} />
    </div>
  );
}

export default BookAppointment;
