import React, { useState } from 'react';

export default function Home() {
  const isAdmin = localStorage.getItem('role') !== 'admin'; // רק לאדמין מוצגות אפשרויות עריכה

  const [services, setServices] = useState([
    { name: 'תספורת קצרה', price: '80 ₪' },
    { name: 'תספורת ארוכה', price: '120 ₪' },
    { name: 'גזירת זקן', price: '50 ₪' },
  ]);

  const [workingHours, setWorkingHours] = useState([
    { day: 'ראשון', start: '09:00', end: '18:00' },
    { day: 'שני', start: '09:00', end: '18:00' },
    { day: 'שלישי', start: '09:00', end: '18:00' },
    { day: 'רביעי', start: '09:00', end: '18:00' },
    { day: 'חמישי', start: '09:00', end: '18:00' },
    { day: 'שישי', start: '09:00', end: '14:00' },
    { day: 'שבת', start: '-', end: '-' },
  ]);

  const [newService, setNewService] = useState({ name: '', price: '' });
  const [newHour, setNewHour] = useState({ day: '', start: '', end: '' });

  // פעולות עריכה
  const addService = () => {
    if (newService.name && newService.price) {
      setServices([...services, newService]);
      setNewService({ name: '', price: '' });
    }
  };

  const removeService = (index) => {
    setServices(services.filter((_, i) => i !== index));
  };

  const addHour = () => {
    if (newHour.day && newHour.start && newHour.end) {
      setWorkingHours([...workingHours, newHour]);
      setNewHour({ day: '', start: '', end: '' });
    }
  };

  const removeHour = (index) => {
    setWorkingHours(workingHours.filter((_, i) => i !== index));
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-white font-sans flex flex-col items-center text-right p-4">
      <div className="w-full max-w-md mx-auto">
        {/* לוגו */}
        <div className="flex flex-col items-center mb-6">
          <img src="/images/logo.png" alt="Barber logo" className="w-24 h-24 object-contain mb-3" />
          <h1 className="text-2xl font-bold text-gray-800">BarberTime</h1>
          <p className="text-gray-600 text-sm">
            טלפון:{' '}
            <a href="tel:0501234567" className="text-blue-500 hover:underline">
              050-1234567
            </a>
          </p>
        </div>

        {/* כפתור קבע תור */}
        <div className="flex justify-center mb-8">
          <button className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-3 px-10 rounded-full shadow-md transition-transform hover:scale-105">
            קבע תור
          </button>
        </div>

        {/* מחירון */}
        <div className="mb-8 bg-white p-4 rounded-lg shadow-md">
          <h2 className="text-xl font-semibold mb-3 text-gray-800">מחירון שירותים</h2>

          {services.map((s, i) => (
            <div key={i} className="flex justify-between items-center py-2 border-b last:border-none">
              <span>{s.name}</span>
              <div className="flex items-center gap-2">
                <span>{s.price}</span>
                {isAdmin && (
                  <button
                    onClick={() => removeService(i)}
                    className="text-red-500 text-sm hover:underline"
                  >
                    הסר
                  </button>
                )}
              </div>
            </div>
          ))}

          {isAdmin && (
            <div className="mt-3 flex gap-2">
              <input
                type="text"
                placeholder="שם שירות"
                value={newService.name}
                onChange={(e) => setNewService({ ...newService, name: e.target.value })}
                className="border p-2 rounded w-1/2 text-sm"
              />
              <input
                type="text"
                placeholder="מחיר"
                value={newService.price}
                onChange={(e) => setNewService({ ...newService, price: e.target.value })}
                className="border p-2 rounded w-1/2 text-sm"
              />
              <button
                onClick={addService}
                className="bg-green-500 text-white px-3 rounded text-sm hover:bg-green-600"
              >
                +
              </button>
            </div>
          )}
        </div>

        {/* שעות פעילות */}
        <div className="mb-8 bg-white p-4 rounded-lg shadow-md">
          <h2 className="text-xl font-semibold mb-3 text-gray-800">שעות פעילות</h2>

          {workingHours.map((wh, i) => (
            <div key={i} className="flex justify-between items-center py-2 border-b last:border-none">
              <span>{wh.day}</span>
              <div className="flex items-center gap-2">
                <span>{wh.start} - {wh.end}</span>
                {isAdmin && (
                  <button
                    onClick={() => removeHour(i)}
                    className="text-red-500 text-sm hover:underline"
                  >
                    הסר
                  </button>
                )}
              </div>
            </div>
          ))}

          {isAdmin && (
            <div className="mt-3 flex flex-col gap-2">
              <input
                type="text"
                placeholder="יום"
                value={newHour.day}
                onChange={(e) => setNewHour({ ...newHour, day: e.target.value })}
                className="border p-2 rounded text-sm"
              />
              <div className="flex gap-2">
                <input
                  type="text"
                  placeholder="מתחיל"
                  value={newHour.start}
                  onChange={(e) => setNewHour({ ...newHour, start: e.target.value })}
                  className="border p-2 rounded w-1/2 text-sm"
                />
                <input
                  type="text"
                  placeholder="מסתיים"
                  value={newHour.end}
                  onChange={(e) => setNewHour({ ...newHour, end: e.target.value })}
                  className="border p-2 rounded w-1/2 text-sm"
                />
                <button
                  onClick={addHour}
                  className="bg-green-500 text-white px-3 rounded text-sm hover:bg-green-600"
                >
                  +
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
