import axios from 'axios';

const API = axios.create({ baseURL: 'http://localhost:5000/api' });

export const requestOTP = phone => API.post('/otp/request', { phone });
export const verifyOTP = (phone, otp) => API.post('/otp/verify', { phone, otp });
export const getMessages = (scope, id) => API.get(`/messages?scope=${scope}&id=${id || ''}`);
export const getBarbers = () => API.get('/admin/barbers');
export const createBarber = data => API.post('/admin/barbers', data);
