import React, { useEffect, useState } from 'react';
import { getBarbers, getMessages } from '../api/api';

export default function Home() {
  const [barbers, setBarbers] = useState([]);
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    getBarbers().then(res => setBarbers(res.data));
    getMessages('global').then(res => setMessages(res.data));
  }, []);

  return (
    <div className="min-h-screen bg-gray-50 font-sans text-right p-4">
      {/* כותרת */}
      <h1 className="text-3xl font-bold mb-4 text-gray-800">ברוכים הבאים ל‑BarberTime</h1>

      {/* קרוסלת הודעות */}
      <div className="mb-6">
        {messages.length > 0 ? (
          <div className="overflow-x-auto flex gap-4">
            {messages.map(msg => (
              <div
                key={msg._id}
                className="min-w-[200px] bg-white shadow rounded p-4 flex-shrink-0"
              >
                <h3 className="font-semibold text-gray-700">{msg.title}</h3>
                <p className="text-gray-500 text-sm mt-1">{msg.body}</p>
              </div>
            ))}
          </div>
        ) : (
          <p className="text-gray-400">אין הודעות כרגע</p>
        )}
      </div>

      {/* רשימת ספרים */}
      <h2 className="text-2xl font-semibold mb-3 text-gray-800">ספרים זמינים</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
        {barbers.map(barber => (
          <div key={barber._id} className="bg-white shadow rounded p-4 flex flex-col">
            <img
              src={barber.image || '/images/default-barber.jpg'}
              alt={barber.name}
              className="w-full h-40 object-cover rounded mb-3"
            />
            <h3 className="font-semibold text-gray-700 mb-1">{barber.name}</h3>
            <p className="text-gray-500 text-sm mb-2">
              {barber.workingHours?.map(w => `${w.day}: ${w.start}-${w.end}`).join(', ') || 'לא צוינה שעות'}
            </p>
            <button className="mt-auto bg-blue-500 hover:bg-blue-600 text-white py-2 rounded transition">
              קבע תור
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
