import React, { useEffect, useState } from 'react';
import { getBarbers, getMessages } from '../api/api';
import { Link } from 'react-router-dom';

export default function Home() {
  const [barbers, setBarbers] = useState([]);
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    getBarbers().then(res => setBarbers(res.data));
    getMessages().then(res => setMessages(res.data));
  }, []);

  return (
    <div className="p-4">
      <h1 className="text-2xl mb-4">ברוכים הבאים לספר שלי</h1>

      <div className="mb-6">
        <h2 className="text-xl mb-2">מבצעים ועדכונים:</h2>
        <ul className="space-y-2">
          {messages.map(msg => (
            <li key={msg.id} className="bg-yellow-100 p-2 rounded">{msg.body}</li>
          ))}
        </ul>
      </div>

      <div>
        <h2 className="text-xl mb-2">בחר ספר:</h2>
        <ul className="space-y-2">
          {barbers.map(barber => (
            <li key={barber.id} className="bg-white p-3 rounded shadow">
              <div className="flex justify-between items-center">
                <span>{barber.name}</span>
                <Link to="/book" className="bg-blue-500 text-white px-3 py-1 rounded">קבע תור</Link>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
