import React, { useEffect, useState } from 'react';
import { getBarbers, getMessages } from '../api/api';

export default function Home() {
  const [barbers, setBarbers] = useState([]);
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    getBarbers().then(res => setBarbers(res.data));
    getMessages('global').then(res => setMessages(res.data));
  }, []);

  return (
    <div className="min-h-screen bg-gray-50 p-4 font-sans text-right">
      {/* כותרת */}
      <h1 className="text-3xl font-bold mb-4">ברוכים הבאים לספרי השיער שלנו</h1>

      {/* חיפוש */}
      <input
        type="text"
        placeholder="חפש ספר או שירות..."
        className="w-full p-3 mb-6 rounded border border-gray-300 focus:outline-none focus:ring focus:ring-blue-300"
      />

      {/* קרוסלת הודעות */}
      <div className="mb-6 overflow-x-auto flex space-x-4">
        {messages.map(msg => (
          <div
            key={msg._id}
            className="flex-shrink-0 w-72 p-4 bg-blue-100 rounded-lg shadow"
          >
            <h3 className="font-semibold mb-2">{msg.title}</h3>
            <p className="text-sm">{msg.body}</p>
          </div>
        ))}
      </div>

      {/* רשימת ספרים */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {barbers.map(barber => (
          <div key={barber._id} className="bg-white p-4 rounded-lg shadow hover:shadow-lg transition">
            <img
              src={barber.image || '/images/default-barber.jpg'}
              alt={barber.name}
              className="w-full h-48 object-cover rounded mb-3"
            />
            <h2 className="text-xl font-semibold">{barber.name}</h2>
            <p className="text-gray-600 text-sm mb-2">{barber.bio}</p>
            <button className="mt-2 w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600 transition">
              קבע תור
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
