import React from "react";
import AdminEditableTable from "../components/AdminEditableTable";

export default function Home() {
  const isAdmin = true; // בעתיד יגיע מ-auth
  const priceList = [
    { name: "תספורת רגילה", price: "80 ₪" },
    { name: "גזירת זקן", price: "50 ₪" },
  ];

  const hours = [
    { day: "ראשון", open: "09:00", close: "18:00" },
    { day: "שני", open: "09:00", close: "18:00" },
  ];

  return (
    <div className="p-4 text-center">
      <img
        src="/logo.png"
        alt="לוגו המספרה"
        className="mx-auto w-24 h-24 rounded-full mb-3 shadow-md"
      />
      <h1 className="text-xl font-bold text-gray-800">BarberTime ✂️</h1>
      <p className="text-gray-600 mb-3">📞 050-1234567</p>

      <button className="bg-blue-500 text-white px-6 py-2 rounded-full shadow hover:bg-blue-600">
        קבע תור
      </button>

      <div className="grid grid-cols-2 gap-3 mt-5">
        <img src="/hair1.jpg" alt="תספורת" className="rounded-lg shadow" />
        <img src="/hair2.jpg" alt="תספורת" className="rounded-lg shadow" />
      </div>

      <AdminEditableTable
        title="💈 מחירון"
        columns={[
          { key: "name", label: "שירות" },
          { key: "price", label: "מחיר" },
        ]}
        data={priceList}
        isAdmin={isAdmin}
        onSave={(rows) => console.log("Updated Price List:", rows)}
      />

      <AdminEditableTable
        title="🕓 שעות פעילות"
        columns={[
          { key: "day", label: "יום" },
          { key: "open", label: "פתיחה" },
          { key: "close", label: "סגירה" },
        ]}
        data={hours}
        isAdmin={isAdmin}
        onSave={(rows) => console.log("Updated Hours:", rows)}
      />
    </div>
  );
}
