import React, { useEffect, useState } from 'react';
import { getBarbers, getMessages } from '../api/api';

export default function Home() {
  const [barbers, setBarbers] = useState([]);
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    getBarbers().then(res => setBarbers(res.data));
    getMessages('global').then(res => setMessages(res.data));
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 font-sans text-right p-4 sm:p-6 lg:p-8">
      <div className="max-w-7xl mx-auto">
        {/* כותרת */}
        <h1 className="text-4xl md:text-5xl font-extrabold mb-8 text-gray-900 text-center tracking-tight">
          ברוכים הבאים ל‑<span className="text-blue-600">BarberTime</span>
        </h1>

        {/* קרוסלת הודעות */}
        <div className="mb-12">
          <h2 className="text-2xl font-bold text-gray-800 mb-4 px-1">הודעות ועדכונים</h2>
          {messages.length > 0 ? (
            <div className="flex overflow-x-auto space-x-4 space-x-reverse pb-4">
              {messages.map(msg => (
                <div
                  key={msg._id}
                  className="min-w-[280px] bg-white shadow-lg rounded-lg p-5 flex-shrink-0 border border-gray-200 transform hover:-translate-y-1 transition-transform duration-300"
                >
                  <h3 className="font-bold text-lg text-gray-800">{msg.title}</h3>
                  <p className="text-gray-600 text-sm mt-2">{msg.body}</p>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-8 bg-white rounded-lg shadow-md">
              <p className="text-gray-500">אין הודעות להצגה כרגע</p>
            </div>
          )}
        </div>

        {/* רשימת ספרים */}
        <h2 className="text-3xl font-bold mb-6 text-gray-900">הספרים שלנו</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
          {barbers.map(barber => (
            <div key={barber._id} className="bg-white shadow-xl rounded-lg overflow-hidden flex flex-col group transform hover:-translate-y-2 transition-transform duration-300">
              <div className="relative">
                <img
                  src={barber.image || '/images/default-barber.jpg'}
                  alt={barber.name}
                  className="w-full h-56 object-cover"
                />
                <div className="absolute inset-0 bg-black bg-opacity-20 group-hover:bg-opacity-10 transition-all duration-300"></div>
              </div>
              <div className="p-5 flex flex-col flex-grow">
                <h3 className="text-xl font-bold text-gray-800 mb-2">{barber.name}</h3>
                <p className="text-gray-600 text-sm mb-4 flex-grow">
                  {barber.workingHours?.length > 0 ? barber.workingHours.map(w => `${w.day}: ${w.start}-${w.end}`).join(' | ') : 'לא הוגדרו שעות עבודה'}
                </p>
                <button className="mt-auto w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg transition-all duration-300 shadow-md hover:shadow-lg">
                  קבע תור
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
