import React, { useEffect, useState } from 'react';
import { getBarbers, getMessages } from '../api/api';
import BarberCard from '../components/BarberCard';
import MessagesFeed from '../components/MessagesFeed';

export default function Home() {
  const [barbers, setBarbers] = useState([]);
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    getBarbers().then(res => setBarbers(res.data));
    getMessages().then(res => setMessages(res.data));
  }, []);

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">ברוכים הבאים לספר שלנו!</h1>
      <MessagesFeed messages={messages} />
      <div className="mt-6 grid grid-cols-1 md:grid-cols-2 gap-4">
        {barbers.map(barber => <BarberCard key={barber.id} barber={barber} />)}
      </div>
    </div>
  );
}
