// src/pages/Home.jsx
import React, { useState } from 'react';

export default function Home({ user }) {
  const [isManagerOpen, setManagerOpen] = useState(false);
  const [hours, setHours] = useState([
    { day: '专砖', from: '09:00', to: '18:00' },
    { day: '砖', from: '09:00', to: '18:00' },
    { day: '砖砖', from: '09:00', to: '18:00' },
    { day: '专注', from: '09:00', to: '18:00' },
    { day: '砖', from: '09:00', to: '18:00' },
    { day: '砖砖', from: '09:00', to: '14:00' },
    { day: '砖转', from: '-', to: '-' },
  ]);
  const [prices, setPrices] = useState([
    { service: '转住驻专转', price: 100 },
    { service: '驻驻', price: 30 },
    { service: '爪注', price: 150 },
  ]);
  const [isEditingHours, setEditingHours] = useState(false);
  const [isEditingPrices, setEditingPrices] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.reload();
  };

  const toggleManager = () => setManagerOpen(!isManagerOpen);

  const updateHours = (index, field, value) => {
    const newHours = [...hours];
    newHours[index][field] = value;
    setHours(newHours);
  };

  const updatePrices = (index, field, value) => {
    const newPrices = [...prices];
    newPrices[index][field] = value;
    setPrices(newPrices);
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col items-center p-4">
      {/*  */}
      <div className="text-4xl font-bold my-4"> 住驻专 砖</div>

      {/* 驻转专 拽注 转专 */}
      <button className="bg-blue-500 text-white px-6 py-3 rounded-md mb-4 hover:bg-blue-600">
        拽注 转专
      </button>

      {/* 专转 转转 */}
      <div className="grid grid-cols-2 md:grid-cols-3 gap-2 mb-6 w-full max-w-md">
        <img src="https://via.placeholder.com/150" alt="转住驻专转 1" className="rounded-md" />
        <img src="https://via.placeholder.com/150" alt="转住驻专转 2" className="rounded-md" />
        <img src="https://via.placeholder.com/150" alt="转住驻专转 3" className="rounded-md" />
        <img src="https://via.placeholder.com/150" alt="转住驻专转 4" className="rounded-md" />
      </div>

      {/* 转 砖注转 驻注转 */}
      <div className="mb-6 w-full max-w-md">
        <div className="flex justify-between items-center mb-2">
          <h2 className="text-xl font-semibold">砖注转 驻注转</h2>
          {user.role === 'admin' && (
            <button
              onClick={() => setEditingHours(!isEditingHours)}
              className="text-sm text-blue-500"
            >
              {isEditingHours ? '砖专' : '注专'}
            </button>
          )}
        </div>
        <table className="w-full border border-gray-300 rounded-md overflow-hidden">
          <tbody>
            {hours.map((h, i) => (
              <tr key={i} className="border-b border-gray-200">
                <td className="p-2">{h.day}</td>
                <td className="p-2">
                  {isEditingHours ? (
                    <input
                      value={h.from}
                      onChange={e => updateHours(i, 'from', e.target.value)}
                      className="border p-1 w-full"
                    />
                  ) : (
                    h.from
                  )}
                </td>
                <td className="p-2">
                  {isEditingHours ? (
                    <input
                      value={h.to}
                      onChange={e => updateHours(i, 'to', e.target.value)}
                      className="border p-1 w-full"
                    />
                  ) : (
                    h.to
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* 转 专 */}
      <div className="mb-6 w-full max-w-md">
        <div className="flex justify-between items-center mb-2">
          <h2 className="text-xl font-semibold">专</h2>
          {user.role === 'admin' && (
            <button
              onClick={() => setEditingPrices(!isEditingPrices)}
              className="text-sm text-blue-500"
            >
              {isEditingPrices ? '砖专' : '注专'}
            </button>
          )}
        </div>
        <table className="w-full border border-gray-300 rounded-md overflow-hidden">
          <tbody>
            {prices.map((p, i) => (
              <tr key={i} className="border-b border-gray-200">
                <td className="p-2">
                  {isEditingPrices ? (
                    <input
                      value={p.service}
                      onChange={e => updatePrices(i, 'service', e.target.value)}
                      className="border p-1 w-full"
                    />
                  ) : (
                    p.service
                  )}
                </td>
                <td className="p-2">
                  {isEditingPrices ? (
                    <input
                      value={p.price}
                      onChange={e => updatePrices(i, 'price', e.target.value)}
                      className="border p-1 w-full"
                    />
                  ) : (
                    `${p.price} `
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Manager Menu */}
      <div className="fixed top-4 right-4">
        <button
          onClick={toggleManager}
          className="bg-gray-200 p-2 rounded-md"
        >
          MANAGER
        </button>
        {isManagerOpen && (
          <div className="mt-2 bg-white border border-gray-300 rounded-md shadow-md p-4">
            <div className="mb-2">砖: {user.name}</div>
            <div className="mb-2">住驻专: {user.phone}</div>
            <button
              onClick={handleLogout}
              className="bg-red-500 text-white px-3 py-1 rounded-md"
            >
              转转拽转
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
